function ScrollFadeIn(t){this.end_percen=t||50;this.el=document.querySelectorAll([".scrollFadeInUp",".scrollFadeInLeft",".scrollFadeInRight",".scrollFadeZoomIn",".scrollFadeZoomOut"]);this.calc_view=function(){this.view_height=window.innerHeight||document.documentElement.clientHeight;this.active_area=this.end_percen/100*this.view_height;this.stop_area=this.view_height-this.active_area};this.calc_percen=function(t){let e=((t-this.stop_area)/this.active_area).toFixed(5);return e};this.calc_scroll=function(){this.el.forEach(s=>{const n=s.getBoundingClientRect().top;if(n<0){return}if(n<=this.view_height){let t=this.calc_percen(n);if(n<=this.stop_area){t=0}let e=t;let i=-t+1;s.style.opacity=.5*i+.5;if(s.classList.contains("scrollFadeInUp")){s.style.transform=`translateY(${100*e}px)`;return}if(s.classList.contains("scrollFadeInLeft")){s.style.transform=`translateX(${100*e}px)`;return}if(s.classList.contains("scrollFadeInRight")){s.style.transform=`translateX(${-100*e}px)`;return}if(s.classList.contains("scrollFadeZoomIn")){s.style.transform=`scale(${.2*i+.8})`;return}if(s.classList.contains("scrollFadeZoomOut")){s.style.transform=`scale(${.2*e+1})`;return}}})};this.init=function(){this.calc_view();this.calc_scroll();let t=false;let e=null;window.addEventListener("scroll",()=>{if(!t){window.requestAnimationFrame(()=>{this.calc_scroll();document.body.style.pointerEvents="none";window.clearTimeout(e);e=window.setTimeout(()=>{document.body.style.pointerEvents="auto"},60);t=false});t=true}},false);window.addEventListener("resize",()=>{if(!t){window.requestAnimationFrame(()=>{this.calc_view();t=false});t=true}},false)};return this}